$(function () {
  
    $(".nav li.dropdown").each(function () {
        $(this).find("ul.sub-menu").addClass("dropdown-menu");
        $(this).append("<a data-toggle='dropdown' class='dropdown-icon hidden-lg hidden-lg2'><span class='caret'></span></a>");

    }),
   
    $('.moreLess').moreLess();
    $('.carousel').carousel();

  $(window).scroll(function(){
        if ($(this).scrollTop() > 500) {
            $('.scrollToTop').fadeIn();
        } else {
            $('.scrollToTop').fadeOut();
        }
    });
    
    //Click event to scroll to top
    $('.scrollToTop').click(function(){
        $('html, body').animate({scrollTop : 0},1000);
        return false;
    });

    $(".owl-carousel").owlCarousel({
        loop: !0,
        margin: 5,
        responsiveClass: !0,
        responsive: {
            0: {
                items: 1,
                nav: !0,
                dots: !1
            },
            600: {
                items: 2,
                nav: !0,
                dots: !1
            },
            1e3: {
                autoplay: !0,
                autoplayTimeout: 2e3,
                autoplayHoverPause: !0,
                items: 3,
                nav: !0,
                loop: !0,
                dots: !1,
                mouseDrag: !0
            }
        }
    });

    setTimeout(function(){
        $('.sticky-post').find('.caption').css({'display':'block'});
    }, 2000);


});

